var archiveurl = "https://corsproxy.io/?https://web.archive.org/save/_embed/"; //root url

function archiveit(a) {
  try {
    return fetch(archiveurl + encodeURIComponent(a));
  } catch (b) {
    console.log("failed to archive the url: " + b);
  }
}
// to archive a specific url, use archiveit("https://example.com")